module.exports.xSmall = {
	fontSize: fontBase * .75
}

module.exports.small = {
	fontSize: fontBase * .875
}

module.exports.medium = {
	fontSize: fontBase * 1
}

module.exports.large = {
	fontSize: fontBase * 1.5
}

module.exports.xLarge = {
	fontSize: fontBase * 2.5
}

module.exports.xxLarge = {
	fontSize: fontBase * 3.5
}